mapper.select:
SELECT
       CODE
  FROM
       TB_AUTHCODE
 WHERE
       REMOTEADDR = #{ remoteAddr }
   AND       
       CODE = #{ authCode }
   AND
   	   EMAIL = #{ email }

mapper.update:
UPDATE TB_MEMBER SET
MEMBER_PWD = #{ memberPwd },
POSTCODE = #{ postCode }, ROADADDRESS = #{ roadAddress }, DETAILADDRESS = #{ detailAddress }, EMAIL = #{ email } WHERE MEMBER_ID = #{memberId}
